INSERT [content_version] ([id], [content_id], [version_id], [state_id], [data], [edit_user], [updated_date]) VALUES (6, 2, 2, 1, '<p>Info about the coding and programming I do. Including C#, Java, ASP (SSJS), Perl and some of the projects we have done.</p>', 1, '07/06/2010 12:44:38')
