INSERT [content_version] ([id], [content_id], [version_id], [state_id], [data], [edit_user], [updated_date]) VALUES (8, 3, 2, 1, '<h2>CCMS</h2>
<p>CCMS, or &#39;Clare&#39;s Content Management System&#39;, is a CMS I have been writing. Originally written in Perl and file-system based I have now ported it to classic ASP, with server-side javascript as the scripting language with versioned content and database support.</p>
<p>&nbsp;</p>', 1, '07/06/2010 12:49:35')
INSERT [content_version] ([id], [content_id], [version_id], [state_id], [data], [edit_user], [updated_date]) VALUES (9, 4, 1, 1, NULL, 1, '12/07/2010 10:57:05')
INSERT [content_version] ([id], [content_id], [version_id], [state_id], [data], [edit_user], [updated_date]) VALUES (10, 4, 2, 1, '<p>Welcome to The Coven...</p>
<p>A bit of a mix here - ranging from bloggy type stuff about gigs etc. we have been to, to historical post-grad stuff to full-on techy stuff.</p>
<p>Have fun...&nbsp;</p>', 1, '12/07/2010 10:59:17')
INSERT [content_version] ([id], [content_id], [version_id], [state_id], [data], [edit_user], [updated_date]) VALUES (11, 4, 3, 1, '<p>Welcome to The Coven...</p>
