INSERT [content_version] ([id], [content_id], [version_id], [state_id], [data], [edit_user], [updated_date]) VALUES (42, 13, 2, 2, '<h1>Current Position</h1>
<p>I am currently employed as a technical consultant by <a title="OpenText Corporate Site" href="http://www.opentext.com" target="_blank">OpenText</a>, a provider of enterprise content management solutions. My historic background is as a web content management technical solution architect, originally employed by Obtree. My current position at OpenText is as a result of the acquisition of Obtree.</p>', 1, '26/08/2010 17:02:23')
